mapper.select:
SELECT c.class_id AS classId, c.class_name AS className, c.association_id AS association, c.start_date AS startDate, c.end_date AS endDate, c.monday AS monday, c.tuesday AS tuesday, c.wednesday AS wednesday, c.thursday AS thursday, c.friday AS friday, c.saturday AS saturday, c.sunday AS sunday, c.active AS active, c.start_time AS startTime, c.end_time AS endTime, c.language AS language, c.regist_datetime AS registDatetime, c.change_datetime AS changeDatetime FROM class c
INNER JOIN user_class uc ON c.class_id = uc.class_id AND uc.user_id = #{userId}
WHERE c.active = #{active}
ORDER BY c.regist_datetime DESC

mapper.insert:
INSERT INTO class_date 
	(
	class_id, 
	class_date
	)
VALUES
	(
	#{classId}, 
	#{classDate}
	)

mapper.delete:
DELETE FROM
	class_date 



			AND class_id = #{classId} 


			AND class_date = #{classDate}

mapper.select:
SELECT uc.user_id AS userId, uc.class_id AS classId, uc.study_goal AS studyGoal, uc.first_evaluation_date AS firstEvaluationDate, uc.first_step_score AS firstStepScore, uc.first_step AS firstStep, uc.last_evaluation_date AS lastEvaluationDate, uc.last_step_score AS lastStepScore, uc.last_step AS lastStep, IFNULL(u.user_name, uc.user_id) AS userName, u.mobile_number AS mobileNumber, u.email AS email FROM ( SELECT * FROM user_class WHERE class_id = #{classId}
AND user_type = #{userType}
AND user_id = #{userId}
) uc LEFT JOIN user u ON uc.user_id = u.user_id

mapper.delete:
DELETE FROM
	user_class 
WHERE


			AND user_id = #{userId}


			AND class_id = #{classId}


			AND user_type = #{userType}

mapper.update:
UPDATE
	class_date
SET
	class_date = #{newDate}
WHERE
	class_id = #{classId} AND class_date = #{oldDate}

UPDATE
	attendance 
SET
	class_date = #{newDate} 
WHERE
	class_id = #{classId} AND class_date = #{oldDate}
